mapper.select:
		select 
		       count(*)
		  from cart
         where mem_no = #{memNo}
           and bk_no = #{bkNo}

mapper.update:
		update
			   cart
		   set 
		   	   cart_qty = cart_qty + #{cartQty}
		 where mem_no = #{memNo}
           and bk_no = #{bkNo}

mapper.insert:
insert
  into cart
  (
    bk_no
  , mem_no
  , cart_qty
  , cart_date
  )
  values
  (
    #{bkNo}
  , #{memNo}
  , #{cartQty}
  , sysdate
  )

mapper.delete:
		delete
		  from cart
		 where mem_no = #{memNo}
           and bk_no = #{bkNo}

mapper.insert:
insert
  into book_interest
 	    (
 		 interest_no,
 		 bk_no
  		)
 values (
 		 (select interest_no
 		    from interest
 		   where interest_content = #{interestContent}
 		 ),
 		 #{bkNo}	
 		)

mapper.delete:
delete
  from book_interest
 where bk_no = #{bkNo}
   and interest_no = (
   					   select interest_no
   					     from interest
   					    where interest_content = #{interestContent}
   					  )

mapper.update:
		update
			   cart
		   set cart_qty = #{cartQty}
		 where mem_no = #{memNo}
           and bk_no = #{bkNo}

mapper.select:
		select 
		       count(*)
		  from list
         where mem_no = #{memNo}
           and bk_no = #{bkNo}

mapper.insert:
insert
  into list
  (
    bk_no
  , mem_no
  , list_date
  )
  values
  (
    #{bkNo}
  , #{memNo}
  , sysdate
  )

mapper.delete:
delete
from list
where mem_no = #{memNo} and bk_no=#{bkNo}
