mapper.insert:
insert 
  into booklist
     (
       bl_no
     , mem_no
     , bk_no
     , bl_title
     , bl_content
     , bl_rate
     , bl_cdate
     )
values
     (
       seq_blno.nextval
     , #{memNo}
     , #{bkNo}
     , #{blTitle}
     , #{blContent}
     , #{blRate}
     , SYSDATE
     )

insert 
  into reply
     ( 
       reply_no
     , mem_no
     , reply_type
     , ref_post
     , reply_content
     , reply_refno
     , depth
     )
values
     ( 
       seq_rno.nextval
     , #{memNo}
     , 2
     , #{refPost}
     , #{replyContent}
     , #{replyRefNo}
     , #{depth}
     )

mapper.update:
update
       booklist
   set 
       bl_title = #{blTitle}
     , bl_content = #{blContent}
     , bk_no = #{bkNo}
     , bl_rate = #{blRate}
     , bl_mdate = SYSDATE
 where bl_no = #{blNo}

mapper.select:
SELECT count(*)
  FROM "LIKE"
 WHERE ref_post = #{refPost}
   AND like_type = 1
   AND mem_no = #{memNo}
