mapper.update:
  		update members
			set
			 email_status = #{emailStatus}
		where mem_email = #{memEmail}

mapper.select:
		select mem_email,
  		email_status
  	from members
  	where mem_email=#{memEmail}
  	and email_status = #{emailStatus}

select mem_id, mem_email, mem_pwd, mem_no
from members
where mem_email = #{memEmail}
and mem_id = #{memId}
and not mem_status = 'N'

mapper.update:
update members
set
 mem_pwd = #{memPwd}
where mem_id = #{memId} and mem_email = #{memEmail}

update members
set 
	mem_work = #{memWork},
	mem_level = #{memLevel}
where 
	mem_no = #{memNo}

mapper.select:
select mem_no, interest_no
from mem_interest
where mem_no = #{memNo}

select mem_no, sub_cate_no
from mem_category
where mem_no = #{memNo}
