mapper.insert:
insert
  into subscription
  	 (
       subsc_no
     , mem_no
     , subsc_name
     , subsc_price
     , subsc_period
     , subsc_sdate
     , subsc_enddate
     , subsc_deldate
     , subsc_status
     , subsc_receiver
     , subsc_post
     , subsc_address
     , address_detail
     , address_ref
     , subsc_phone
     , deliveryrequest
     )
values
     ( 
       SEQ_SUBNO.nextval
     , #{memNo}
     , #{subscName}
     , #{subscPrice}
     , #{subscPeriod}
     , #{subscSdate}
     , #{subscEndDate}
     , #{subscDelDate}
     , 'Y'
     , #{subscReceiver}
     , #{subscPost}
     , #{subscAddress}
     , #{addressDetail}
     , #{addressRef}
     , #{subscPhone}
     , #{deliveryRequest}
     )

mapper.update:
update
       subscription
   set admin_memo = #{adminMemo}
 where subsc_no = #{subscNo}

mapper.select:
select
       count(*) count
  from subsc_bookorder
  join book using (bk_no)
 where subsc_no = #{sNo}
   and bk_no = #{bkNo}
